ä»£ç  # class Solution { public int search(int[] nums, int target) { int left = 0, right = nums.length; while (left &lt; right) { int medium = left + (right - left) / 2; if (nums[medium] == target) return medium; else if (nums[medium] &gt; target) right = medium; else left = medium + 1; } return -1; } }

